import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Login from "./signIn";
import axios from 'axios'
import {ToastsContainer, ToastsStore} from 'react-toasts';


export default class SignUp extends Component {
    path
    constructor(props) {
        super(props)
        this.state = {
            firstname: '',
            lastname: '',
            mail: '',
            pass: '',
            submit: false
        }
        this.onFirstName = this.onFirstName.bind(this)
        this.onLastName = this.onLastName.bind(this)
        this.onEmail = this.onEmail.bind(this)
        this.onPassword = this.onPassword.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    onFirstName = (e) => {
        this.setState({ firstname: e.target.value })
    }

    onLastName = (e) => {
        this.setState({ lastname: e.target.value })
    }

    onEmail = (e) => {
        this.setState({ mail: e.target.value })
    }

    onPassword = (e) => {
        this.setState({ pass: e.target.value })
    }

    handleSubmit = () => {

        if (this.state.pass.length >= 6) {
            let self = this

            axios.post('http://localhost:8000/signup',
                {
                    firstName: this.state.firstname,
                    lastName: this.state.lastname,
                    email: this.state.mail,
                    password: this.state.pass,
                    isLoggedIn:false
                })
                .then(res =>{
                    if (res.data.message === 'SUCCESSFULLY SIGN-UP') {
                                // alert(res.data.message)

                                ToastsStore.success(res.data.message)
                                ToastsContainer.store={ToastsStore}
                                self.setState({ submit: true })
                                self.path='/signin'
                                console.log(self.state.submit)


                 } 
                // .then(function (response) {
                //     if (response.data.message === 'SUCCESSFULLY SIGN-UP') {
                //         alert(response.data.message)
                //         self.setState({ submit: true })
                //         console.log(self.state.submit)


                //     }
                    else {
                        alert(res.data.message)
                    self.setState({ submit: false })
                    console.log(self.state.submit)

                    }

                })
                .catch(function (error) {
                    console.log(error);



                })

        }
        else {
            alert('password must be greater than 6 characters')
        }
        this.setState({mail:this.state.mail})
    }



    render() {
        return (
            <form >
                <div>
                    <h3>Sign Up</h3>

                    <div className="form-group">
                        <label>First name</label>
                        <input type="text" className="form-control" placeholder="First name" onChange={this.onFirstName} value={this.state.firstname} />
                    </div>

                    <div className="form-group">
                        <label>Last name</label>
                        <input type="text" className="form-control" placeholder="Last name" onChange={this.onLastName} value={this.state.lastname} />
                    </div>

                    <div className="form-group">
                        <label>Email address</label>
                        <input type="email" className="form-control" placeholder="Enter email" onChange={this.onEmail} value={this.state.mail} />
                    </div>

                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" id="pw" className="form-control" placeholder="Enter password" onChange={this.onPassword} value={this.state.pass} />
                    </div>

                    <Link to={this.state.submit ? '/signin' : '/signup'} >
                        <button type='submit' className="btn btn-primary btn-block"
                         onClick={this.handleSubmit}>Sign Up</button>
                    </Link>
                    <ToastsContainer store={ToastsStore}/>
                    {/* <ToastsContainer id='toast'></ToastsContainer> */}

                    <p className="forgot-password text-right">

                        Already registered <Link className="nav-link" to={"/signin"}>Login</Link>
                        <Switch>
                            <Route path="/signin" component={Login} />
                        </Switch>
                    </p>
                </div>

            </form>
        );
    }
}
