import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import SignUp from "./signUp";
import axios from 'axios'

export default class Login extends Component {
    constructor(props)
    {
        super(props)
        this.state={
            mail:'',
            pass:''
          }
        this.handleEmail=this.handleEmail.bind(this)
        this.handlePassword=this.handlePassword.bind(this)
    }

    handleEmail=(e)=>
    {
        this.setState({mail:e.target.value})
    }


    handlePassword=(e)=>
    {
        this.setState({pass:e.target.value})
    }


    handleSubmit()
    {   alert('SUBMITTED')
    axios.post('http://localhost:3000/signin',
    {
        email:this.state.mail,
        password:this.state.pass
    }).then(function(response){
        if(response.data.message==='SUCCESSFULLY LOGGED-IN')
        {
            alert(response.data.message)
        }
    })

    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email" value={this.state.mail} onChange={this.handleEmail} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" value={this.state.pass} onChange={this.handlePassword} />
                </div>



                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                <p className="forgot-password text-right">

                Not Registered?  <Link className="nav-link" to={"/sign-up"}>SignUp</Link>
                    <Switch>
                    <Route path="/sign-up" component={SignUp} />
                    </Switch>
                    </p>
                {/* <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p> */}
            </form>
        );
    }
}
