import React from 'react'
import { ToastsContainer, ToastsStore, ToastsContainerPosition } from 'react-toasts';
import axios from 'axios'
import { BrowserRouter as Router, Switch, Route, Link, Redirect } from "react-router-dom";
import {
  Container as ReactContainer,
  Row as ReactRow,
  Col as ReactCol,
} from 'react-grid';

import './Profile.css'



const styles = {
  breakpoints: { xs: 0, sm: 576, md: 768, lg: 992, xl: 1200 },
  containerMaxWidths: { sm: 540, md: 720, lg: 960, xl: 1140 },
  columns: 12,
  gutterWidth: 30
};
export const Container = props => <ReactContainer {...props} styles={styles} />;
export const Row = props => <ReactRow {...props} styles={styles} />;
export const Col = props => <ReactCol {...props} styles={styles} />;

export default class Profile extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      firstname: this.props.location.state.firstName,
      lastname: this.props.location.state.lastName,
      mail: this.props.location.state.email,
      submit: false,
      selectedFile: null

    }
    this.handleLogout = this.handleLogout.bind(this)

  }

  handleLogout = async () => {
    let self = this
    let res = await axios.post('http://localhost:8000/signout',
      { email: this.state.mail }
    )
    if (res.data.success) {
      console.log(res.data.message)
      ToastsStore.error(res.data.message)
      await self.setState({ submit: true, firstname: '', lastname: '', mail: '' })
      self.setState({ submit: true })

    }

  }

  onFileChange = event => {

    this.setState({ selectedFile: event.target.files[0] });

  };

  onFileUpload = () => {

    const formData = new FormData();

    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    console.log(this.state.selectedFile);
    axios.post("api/uploadfile", formData);
  };

  fileData = () => {

    if (this.state.selectedFile) {

      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {this.state.selectedFile.name}</p>
          <p>File Type: {this.state.selectedFile.type}</p>
          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      );
    }
  }


  render() {
    return (
      <div >
        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container" style={{ textTransform: "uppercase" }}>Welcome &nbsp; <b><i>{this.state.firstname}</i></b>
            {/* <Link className="navbar-brand" to={"/signin"}>uploading@s3</Link> */}
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
              <div className="navbar-nav ml-auto">
                <button className="btn btn-primary btn-block" onClick={this.handleLogout}>LOGOUT</button>

                <ToastsContainer store={ToastsStore} />
              </div>

            </div>

          </div>

        </nav>

        <div className="profileHeader"   >

        UPLOAD FILES FROM YOUR DEVICE: <input type="file" className="btn btn-primary btn-block"  onChange={this.onFileChange} />


          <div>

            {/* <input type="file" id="fileInput">
              <script>
                document.getElementById('fileInput').addEventListener('change', function selectedFileChanged()
                  {
                     console.log(this.files)
                  });

              </script>
            </input> */}




            <button className="btn btn-primary btn-block" onClick={this.onFileChange}>CLICK HERE</button><br></br><br></br>
            <div>
              <Container className='cont'>
                <Row>
                <Col >FILE NAME</Col>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <Col >FILE TYPE</Col>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <Col >FILE SIZE</Col>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <Col >UPLOADED ON</Col>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <Col >ACTION</Col>

                         </Row>
              </Container>
            </div>
          </div>
        </div>
        {this.state.submit ? <Redirect to={`/signin`} /> : null}
      </div>


    )
  }

}
