import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Login from "./signIn";
import axios from 'axios'

export default class SignUp extends Component {

    constructor(props)
    {
        super(props)
        this.state={
            firstname:'',
            lastname:'',
            mail:'',
            pass:'',
            submit:false
        }
        this.onFirstName=this.onFirstName.bind(this)
        this.onLastName=this.onLastName.bind(this)
        this.onEmail=this.onEmail.bind(this)
        this.onPassword=this.onPassword.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    onFirstName=(e)=>{
        this.setState({firstname:e.target.value})
    }

    onLastName=(e)=>{
        this.setState({lastname:e.target.value})
    }

    onEmail=(e)=>{
        this.setState({mail:e.target.value})
    }

    onPassword=(e)=>{
        this.setState({pass:e.target.value})
    }

    handleSubmit()
    {   
        if(this.state!==null){
        console.log('ENTERED')
        let self=this
        axios.post('http://localhost:8000/signup',
        {
            firstName:this.state.firstname,
            lastName:this.state.lastname,
            email:this.state.mail,
            password:this.state.pass
        })
        .then(function (response) {  
            if(response.data.message==='SUCCESSFULLY SIGN-UP'){
            self.setState({ submit: true})  
            console.log('ENTERED-SUCCESS')
               alert(response.data.message)
            }
               else{
                  self.setState({submit:false})
                  alert(response.data.message)
                  console.log('ENTERED-UNSUCCESS')
               }

        })
        .catch(function (error) {
          console.log(error);



      })}
      else{
          alert('ALL FIEdLS ARE MANDATORY')
      }
    }



    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>First name</label>
                    <input type="text" className="form-control" placeholder="First name" onChange={this.onFirstName} value={this.state.firstname}/>
                </div>

                <div className="form-group">
                    <label>Last name</label>
                    <input type="text" className="form-control" placeholder="Last name" onChange={this.onLastName} value={this.state.lastname}/>
                </div>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email" onChange={this.onEmail} value={this.state.mail}/>
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" onChange={this.onPassword} value={this.state.pass}/>
                </div>
                {/* <Link className="nav-link" to={"/sign-in"}> */}
                     <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                {/* </Link>
               {this.state.submit?<Switch><Route path="/sign-in" component={Login}/></Switch>:null} */}
                <p className="forgot-password text-right">
                    Already registered <Link className="nav-link" to={"/sign-in"}>Login</Link>
                    <Switch>
                    <Route path="/sign-in" component={Login} />
                    </Switch>
                </p>
            </form>
        );
    }
}
