    //   }

    //   let updatedList = await deleteData(user)
    //   if(updatedList.data.success)
    //   {
    //   this.setState({submit:false})
    //   let getData = await getdata(user)
    //   if(getData.data.success)
    //   {
    //     this.setState({dataList:getData.data.data})
