import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import SignUp from "./signUp";
import Profile from "./Profile"
import axios from 'axios'

export default class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            mail: '',
            pass: '',
            submit: false
        }
        this.handleEmail = this.handleEmail.bind(this)
        this.handlePassword = this.handlePassword.bind(this)
    }

    handleEmail = (e) => {
        this.setState({ mail: e.target.value })
    }


    handlePassword = (e) => {
        this.setState({ pass: e.target.value })
    }


    handleSubmit = () => {

        let self = this

        axios.post('http://localhost:8000/signin',
            {
                email: this.state.mail,
                password: this.state.pass
            })
            .then(function (response) {
                if (response.data.message === 'SUCCESSFULLY LOGGED-IN') {
                    self.setState({ submit: !self.state.submit })
                    console.log(self.state.submit)
                    alert(response.data.message)
                    self.state({submit})

                }
                else {
                    alert(response.data.message)
                    self.setState({ submit: self.state.submit })
                    console.log(self.state.submit)
                }

            })
            .catch(function (error) {
                console.log(error);
            })

    }

    render() {
        return (
            <form >
                <div>
                    <h3>Sign In</h3>

                    <div className="form-group">
                        <label>Email address</label>
                        <input type="email" className="form-control" placeholder="Enter email" value={this.state.mail} onChange={this.handleEmail} />
                    </div>

                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" className="form-control" placeholder="Enter password" value={this.state.pass} onChange={this.handlePassword} />
                    </div>


                    <Link to={this.state.submit ? '/profile':'/signin'} >
                        <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit}>Submit</button>
                    </Link>
                    <p className="forgot-password text-right">
                        Not Registered?  <Link className="nav-link" to={"/signup"}>SignUp</Link>
                        <Switch>
                            <Route path="/signup" component={SignUp} />
                            <Route path="/profile" component={Profile}/>
                        </Switch>
                    </p>
                </div>

                {/* <p className="forgot-password text-right">
                Forgot <a href="#">password?</a>
            </p> */}
            </form>
        );
    }
}
