import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import SignUp from "./signUp";
import Profile from "./Profile"
import axios from 'axios'
import { ToastsContainer, ToastsStore, ToastsContainerPosition } from 'react-toasts';

export default class Login extends Component {

    constructor(props) {
        super(props)
        this.state = {
            mail: '',
            pass: '',
            submit: false
        }
        this.handleEmail = this.handleEmail.bind(this)
        this.handlePassword = this.handlePassword.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleabc = this.handleabc.bind(this)
    }

    handleEmail = (e) => {
        this.setState({ mail: e.target.value })
    }


    handlePassword = (e) => {
        this.setState({ pass: e.target.value })
    }


    handleSubmit = async () => {

        let self = this

        let res = await axios.post('http://localhost:8000/signin',
            {

                email: this.state.mail,
                password: this.state.pass
            })

        if (res.data.message === 'SUCCESSFULLY LOGGED-IN') {


            ToastsStore.success(res.data.message)
            await self.setState({ submit: true })
            console.log(self.state.submit)
            self.setState({ mail: '', pass: '' })


        }
        else {

            ToastsStore.error(res.data.message)
            await self.setState({ submit: false })
            console.log(self.state.submit)

        }
this.handleabc()
    }

    handleabc() {

        // this.handleSubmit()
        this.setState({ submit: this.state.submit })

        this.setState({ submit: this.state.submit })
        console.log('aaa')
        this.setState({ submit: this.state.submit })
        console.log('bb')
        this.setState({ submit: this.state.submit })
        console.log('aaa')
        this.setState({ submit: this.state.submit })
        console.log('aaa')
        this.setState({ submit: this.state.submit })

        console.log('aaa')
    }

    render() {
        return (
            <div>
            <Switch>
            <Route path="/signup" component={SignUp} />
            <Route path="/profile" component={Profile}/>
            </Switch>
            <form >
                <div>
                    <h3>Sign In</h3>

                    <div className="form-group">
                        <label>Email address</label>
                        <input type="email" className="form-control" placeholder="Enter email" value={this.state.mail} onChange={this.handleEmail} autoComplete='on' />
                    </div>

                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" className="form-control" placeholder="Enter password" value={this.state.pass} onChange={this.handlePassword} autoComplete='off' />
                    </div>

                    {this.state.submit ? <Link to='/profile'>
                        <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit}>Submit</button>
                    </Link> :
                        <Link to='/signin'>
                            <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit}>Submit</button>
                        </Link>}


                    <ToastsContainer store={ToastsStore} position={ToastsContainerPosition.TOP_RIGHT} />
                    <p className="forgot-password text-right">
                        Not Registered?  <Link className="nav-link" to="/signup">SignUp</Link>

                    </p>

                </div>

                {/* <p className="forgot-password text-right">
                Forgot <a href="#">password?</a>
            </p> */}
            </form>
            </div>
        );
    }
}
