import React from "react";
import {
  ToastsContainer,
  ToastsStore,
  ToastsContainerPosition,
} from "react-toasts";
import axios from "axios";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";
import {
  Container as ReactContainer,
  Row as ReactRow,
  Col as ReactCol,
  breakpointNext,
} from "react-grid";
import moment from "moment";
import "./Profile.css";

const styles = {
  breakpoints: { xs: 0, sm: 576, md: 768, lg: 992, xl: 1200 },
  containerMaxWidths: { sm: 540, md: 720, lg: 960, xl: 1140 },
  columns: 12,
  gutterWidth: 30,
};
export const Container = (props) => (
  <ReactContainer {...props} styles={styles} />
);
export const Row = (props) => <ReactRow {...props} styles={styles} />;
export const Col = (props) => <ReactCol {...props} styles={styles} />;

export default class Profile extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstname: this.props.location.state.firstName,
      lastname: this.props.location.state.lastName,
      mail: this.props.location.state.email,
      submit: false,
      selectedFile: null,
      date: new Date(),
      dataList: []
    };
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleLogout = async () => {
    let self = this;
    let res = await axios.post("http://localhost:8000/signout", {
      email: this.state.mail,
    });
    if (res.data.success) {
      console.log(res.data.message);
      ToastsStore.error(res.data.message);
      await self.setState({
        submit: true,
        firstname: "",
        lastname: "",
        mail: "",
      });
      self.setState({ submit: true });
    }
  };

  // onFileChange = (event) => {
  //   this.setState({ selectedFile: event.target.files[0] });
  //   //  console.log(this.state.selectedFile);
  // };

  onFileUpload = (ev) => {
    let self = this

    ev.preventDefault();

    const data = new FormData();
    data.append("file", this.uploadInput.files[0]);
    data.append('email', this.state.mail)
    console.log("data", data);


    // axios.post()
    // axios()
    axios({
      method: "post",
      url: "http://localhost:8000/upload",
      data: data,
      // email:this.state.email,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then(function (response) {

        console.log(response);
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });


    axios({
      method: "get",
      url: "http://localhost:8000/getData",
      params: { email: this.state.mail },



    }).then(function (response) {
      //handle success
      console.log(response.data);
      self.setState({ dataList: response.data.data })
      console.log(self.state.dataList)

    })
      .catch(function (response) {
        //handle error
        console.log(response);
      });

  };

  onDelete(key) {

  }

  render() {
    //   let self=this
    //  axios({
    //   method:"get",
    //   url:"http://localhost:8000/getData",
    //   params:{email:this.state.mail},
    // }).then(function (response) {
    //   //handle success
    //   console.log(response.data);
    //   self.setState({dataList:response.data})
    // })
    // .catch(function (response) {
    //   //handle error
    //   console.log(response);
    // });

    let uData = this.state.dataList

    return (

      <div>
        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container" style={{ textTransform: "uppercase" }}>
            <img src={process.env.PUBLIC_URL + '/logo192.png'} style={{ blockSize: '1cm' }} />

            Welcome &nbsp;{" "}
            <b>
              <i>{this.state.firstname}</i>
            </b>
            {/* <Link className="navbar-brand" to={"/signin"}>uploading@s3</Link> */}
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
              <div className="navbar-nav ml-auto">
                <button
                  className="btn btn-primary btn-block"
                  onClick={this.handleLogout}
                >
                  LOGOUT
                </button>

                <ToastsContainer store={ToastsStore} />
              </div>
            </div>
          </div>
        </nav>

        <div className="profileHeader">
          <form onSubmit={this.onFileUpload}>
            <div>
              UPLOAD FILE:{" "}
              <input
                type="file"
                className="btn btn-primary "
                accept=".png, .jpeg, .jpg, .pdf, .doc"
                ref={(ref) => {
                  this.uploadInput = ref;
                }}
              />
              <br></br>
              <br></br>
              <button className="btn btn-primary">UPLOAD TO s3</button>
            </div>
          </form>
          <br></br>
          <br></br>
          <div>
            <div>
              <Container className="cont">

                <Row className="rowHead">
                  <Col ><b>FILE NAME</b></Col>
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col ><b>FILE TYPE</b></Col>
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col ><b>FILE SIZE</b></Col>
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col className="col1"><b>UPLOADED ON</b></Col>
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col><b>ACTION</b></Col>
                </Row>

                {uData.map((item, key) =>


                  <Row >
                    <Col >{item.fName}</Col>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
             <Col> {item.fType}</Col>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col>{item.fSize}</Col>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
             <Col>{item.fDate}</Col>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Col><a href={item.file}>Download</a> <button onClick={this.onDelete}>delete</button> </Col>
                  </Row>
                )}
              </Container>
            </div>
          </div>
        </div>
        {this.state.submit ? <Redirect to={`/signin`} /> : null}
      </div>
    );
  }
}
